                <div class="flex gap-4">
                    <div class="w-1/2 space-y-2">
                    <flux:label>Expiration Date</flux:label>
                    <flux:input placeholder="MM / YY" />
                    </div>
                    <div class="w-1/2 space-y-2">
                    <flux:label>CVC</flux:label>
                    <flux:input placeholder="CVC" />
                    </div>
                </div>

                <div class="space-y-2">
                    <flux:label>Cardholder Name</flux:label>
                    <flux:input placeholder="Name on Card" />
                </div>

                <!-- Default Payment Method Checkbox -->
                <p class="text-sm font-sans">By providing your card information, you allow Eventure to charge your card for future payments in accordance with our terms.</p>
                <div class="flex items-center gap-2">
                    <flux:checkbox />
                    <span class="text-sm">Set as default payment method</span>
                </div>

                <!-- Terms and Policies -->
                <p class="text-sm text-gray-600">
                    By adding your card, you agree to our
                    <a href="/terms" class="text-primary-600 underline">Terms of Service</a> and
                    <a href="/privacy" class="text-primary-600 underline">Privacy Policy</a>.
                </p>

                <!-- Action Buttons -->
                <div class="flex flex-col gap-2">
                    <flux:button type="submit" variant="primary" class="w-full">Add Card</flux:button>
                    <flux:modal.close>
                    <flux:button variant="ghost" class="w-full">Cancel</flux:button>
                    </flux:modal.close>
                </div>
                </div>
            </div>
        </flux:modal>


        <!-- Billing Information Section -->
             <!-- Billing Information Section -->
        <div class="w-full rounded-lg border border-gray-200 p-6">
            <h2 class="mb-4 text-lg font-semibold">Billing Information</h2>
            <div class="mb-4 flex items-center text-gray-500 bg-gray-100 rounded-sm p-5">
                <svg class="mr-2 h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
