        <!-- Modal for Adding Billing Address -->
        <flux:modal name="add-billing-address" class="md:w-[500px]">
            <div class="space-y-6">
                <div>
                    <flux:heading size="lg">Add Billing Information</flux:heading>
                    <p class="mt-2 text-sm text-gray-600">This address will be used as your billing address.</p>
                </div>

                <div class="space-y-4">
                    <!-- Address Form -->
                    <div class="space-y-2">
                        <flux:label required>Address Line 1</flux:label>
                        <flux:input placeholder="Street address" />
                    </div>

                    <div class="space-y-2">
                        <flux:label>Address Line 2</flux:label>
                        <flux:input placeholder="Apartment, suite, etc. (optional)" />
                    </div>

                    <div class="space-y-2">
                        <flux:label required>Country</flux:label>
                        <flux:select>
                            <option value="uganda">Uganda</option>
