                        </flux:select>
                    </div>

                    <div class="space-y-2">
                        <flux:label>State/Province</flux:label>
                        <flux:input placeholder="State/Province" />
                    </div>

                    <div class="grid grid-cols-2 gap-4">
                        <div class="space-y-2">
                            <flux:label required>City/Suburb</flux:label>
                            <flux:input placeholder="City/Suburb" />
                        </div>
                        <div class="space-y-2">
                            <flux:label required>ZIP/Post Code</flux:label>
                            <flux:input placeholder="ZIP/Post Code" />
                        </div>
                    </div>

