                    <div class="flex flex-col gap-2 pt-4">
                        <flux:button type="submit" variant="primary" class="w-full">Update</flux:button>
                        <flux:modal.close>
                            <flux:button variant="ghost" class="w-full">Cancel</flux:button>
                        </flux:modal.close>
                    </div>
                </div>
            </div>
